package Basic_Programs;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=37;
        int i=8;		
		System.out.println(a++) ;
		System.out.println(a--);
		System.out.println(a++ - a--);
	
		//+ ++a - a++ + ++a);
		
		System.out.println((i++) - (--i) + (i--) - (++i) + (i--));

	
	}

}
